if (global.GENTLY) require = GENTLY.hijack(require);
//获得命令行外部参数
function MiniArgvs() {
  if (!(this instanceof MiniArgvs)) return new MiniArgvs();
  var params = {};
  var argv = process.argv;
  for(var index in argv){
      var item = argv[index];
      var start = item.indexOf("--");
      if(start>=0){
          var end = item.indexOf("=");
          var key = item.substring(start+2,end);
          var value = item.substring(end+1,item.length);
          params[key] = value;
      }
  }
  this.items = params; 
  return this;
};
MiniArgvs.prototype.get = function(key){
   return this.items[key];
} 
exports.MiniArgvs = MiniArgvs;